import React from 'react';

// COMPONENTS
import { Parallax, ParallaxLayer } from 'react-spring/renderprops-addons';
import MusicParallax from './MusicParallax';
import PhotographyParallax from './PhotographyParallax';

// CSS
import '../css/Interests.css'

export default class Interests extends React.Component {

    state = {

        active: []

    }

    setActive = category => {
        console.log( category )
        this.Parallax.scrollTo(0)
        this.Parallax.scrollTo(2)
    }

    render() {

        return (

            <div className = 'Body'>

                {/* <ul>
                    <li>Sci-fi</li>
                    <li>Garageband / music making</li>
                    <li>Photography</li>
                    <li>Reading</li>
                </ul> */}

                <div className = 'Selection'>
                    <h4 onClick={() => this.setActive(  )} className = { this.state.active }>Photography</h4>
                    <h4 onClick={() => this.Parallax.scrollTo(2)} className = { this.state.active }>Music</h4>
                </div>

                <Parallax 
                    className = 'Parallax' 
                    ref={ ref => ( this.Parallax = ref ) } 
                    pages={2} 
                    horizontal = {true}
                    style = {{ 'height': '90vh' , 'width': '100%' }}>

                    <div className = 'InterestsParallax'>

                        {/* PHOTOGRAPHY PARALLAX */}

                       <PhotographyParallax/>

                        <MusicParallax/>

                    </div>

                </Parallax>

            </div>

        )
    }

}
